session.css:
.knob-strip .knob-container {
right: 0;
left: auto;
border-radius: 37rem / 45rem;
}
.knob-strip .input-container {
  z-index: 0;
  --alpha-stroke: 0;
}

.text-container {
  --widget-padding: -1rem;
  --alpha-stroke: 0;
  --alpha-fill-off: 0.1;
}
.modal-container,
.button-container {
  --alpha-stroke: 0;
  --alpha-fill-off: 0.15;
  --widget-padding: -1rem;
}

.input-container {
  --alpha-stroke: 0; 
  --alpha-fill-off: 0.05;
}
.button-container.no-pulse inner:after {
  animation: none
}

