}

widgets.onValue:
var v = stateGet("config_modal")
delete v.config_modal
storage.setItem("osc-ardour-6.config", v)

widgets.css:
:host {
  JS{{
var name = @{chan_@{this.variables.n}}
var empty = name === ' '
if (empty) return 'opacity:0.5;pointer-events:none;'
}}
border-right: 1rem solid var(--bg-dark);
}

:host {
  font-size: 80%;
}
> inner > .widget {
flex-shrink: 0!important;
}
.button-container:last-child label {
  justify-content: left;
}

class: pan_fader;
:host {
  margin-bottom: -5rem;
  JS{if (OSC{/strip/pan_type} == 'none') {
    return '--color-widget: transparent; opacity: 0.75; pointer-events: none;'  
  }}  
}

widgets.label:
JS{
var max = VAR{max, -193}
return max == -193 ? '-inf' : parseFloat(max).toFixed(1)
}

widgets.onValue:
var max = getVar(locals.peak, 'max')
if (value > max) {
  setVar(locals.peak, 'max', value)
}

